// src/components/Login.jsx
// üîê ÏïÑÏ£º Í∞ÑÎã®Ìïú Îç∞Î™®Ïö© Î°úÍ∑∏Ïù∏ ÌôîÎ©¥

import { useState } from "react";
import "../styles/Dashboard.css"; // Ìè∞Ìä∏/Î∞∞Í≤Ω Ïû¨ÏÇ¨Ïö©Ïö© (ÏõêÌïòÎ©¥ Î∂ÑÎ¶¨ Í∞ÄÎä•)

function Login({ onLogin }) {
  const [role, setRole] = useState("guest"); // 'guest' | 'admin'
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Îç∞Î™®Ïö©: admin ÎπÑÎ≤àÏùÄ Í≥†Ï†ï Î¨∏ÏûêÏó¥ (ÏßÑÏßú ÏÑúÎπÑÏä§ÏóêÏÑúÎäî Ïù¥Î†áÍ≤å ÌïòÎ©¥ Ïïà Îê®)
    if (role === "admin" && password !== "landmine123") {
      setError("Wrong admin password.");
      return;
    }

    setError("");
    onLogin(role); // App Ï™ΩÏóê Ïó≠Ìï† Ï†ÑÎã¨
  };

  return (
    <div className="login-root">
      <div className="login-card">
        <h1 className="login-title">LandMine Monitoring</h1>
        <p className="login-subtitle">
          Sign in as a guest or administrator to access the dashboard.
        </p>

        <form onSubmit={handleSubmit} className="login-form">
          <label className="login-label">Select role</label>
          <div className="login-role-row">
            <button
              type="button"
              className={
                "login-role-btn" + (role === "guest" ? " login-role-btn-active" : "")
              }
              onClick={() => setRole("guest")}
            >
              Guest
            </button>
            <button
              type="button"
              className={
                "login-role-btn" + (role === "admin" ? " login-role-btn-active" : "")
              }
              onClick={() => setRole("admin")}
            >
              Admin
            </button>
          </div>

          {role === "admin" && (
            <>
              <label className="login-label" htmlFor="password">
                Admin password
              </label>
              <input
                id="password"
                type="password"
                className="login-input"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter admin password"
              />
            </>
          )}

          {error && <p className="login-error">{error}</p>}

          <button type="submit" className="login-submit">
            Enter dashboard
          </button>
        </form>

        <p className="login-hint">
          Demo password for admin: <code>landmine123</code>
        </p>
      </div>
    </div>
  );
}

export default Login;
